mapper.select:
SELECT *, id AS authInfo_id 
FROM phone_auth 
WHERE mobileNumber = #{mobileNumber}
	AND authCode = #{authCode}
	AND status = '0'
	AND type = #{type}

mapper.update:
UPDATE phone_auth 
SET status = '1'
WHERE id = #{id}

UPDATE phone_auth
SET status = #{status},deadline = #{deadline}
WHERE id = #{id}

mapper.select:
SELECT r.*,r.id AS authInfo_id
FROM phone_auth r

	
		AND r.type= #{type}
	
	
		AND r.source= #{source}
	
	
		AND r.mobileNumber = #{mnumber}
	

ORDER BY r.generationTime DESC
