mapper.select:
SELECT *, id AS userbill_id 
FROM user_bill

	
		AND time = #{params.entity.time}
	
	
		AND type = #{params.entity.type}
	
	
		AND type_info = #{params.entity.typeInfo}
	
	
		AND money = #{params.entity.money}
	
	
		AND detail = #{params.entity.detail}
	
	
		AND user_id = #{params.entity.userId}
	

ORDER BY time DESC

SELECT SUM(money) FROM user_bill 
WHERE user_id = #{userId}
	AND type = 'pt_balance'

SELECT SUM(money) 
FROM user_bill
WHERE user_id = #{userId}
	AND type=#{type}

SELECT r.*,r.id AS userbill_id,u.realname as realname
FROM user_bill r JOIN user u ON r.user_id = u.id

    
		AND r.user_id like '%${userId}%'
	
	
	
		AND u.mobile_number = #{mnumber}
	
	
		AND u.realname like '%${realname}%'
	
	
		AND r.time >= #{start}
	
	
		AND r.time <= #{end}
	

ORDER BY r.time DESC
